let tash=[{name:"  תש d מידע ודיגיטל",list:[{quatsion:"a",index:7},{quatsion:'דו"ח 1- האם קיימים דיווחי מפקדים לכל החיילים. האם קיימים תחקירים לשינויי דיווח מהותיים (חופשות מערכות מידע קבע- תיקון לאחר יותר משבוע, נפקדות). האם קיימים חיילים ללא דיווח נוכחות.',index:8},{quatsion:"דיווחים דיגיטליים אחוז דיווח חיילים באפליקציה, מהם מנגנוני הבקרה והמעקב על הטמעת דיווח נוכחות בדיגיטל, דיווח עצמי במקרים אסורים (חופשהחופשת משמרתמחלה וכו').",index:9},{quatsion:'הזנות דיגיטליות - שימוש במערכת תנופה להזנות חו"ללימודיםגני-ילדים וכו',index:10},{quatsion:'תהליכי קליטה דיגיטליים- הליך קליטה שלם ומלא (דף מידע מלא לחייל, ראיון מ.מראיין (SI A+תוכן כולל מוטיבציה והתאמה), ראיון ק.משא"ן (SI.A+תוכן כולל רקע מקצועי, מוטיבציה לשירות באופי היחידה ומידת התאמה לשיבוץ), ט"ט בדיגיטל-לומדות והפצת תחנות חובה, נוהל אלרגנים (הפצות לבעלי תפקידים-מטבח, אפסנאות, רפואה, מפקד החייל המיועד)',index:11}]},{name:"מעקב ובקרה שוטפת",list:[{quatsion:'ראיונות ביוזמת היחידה- ראיונות קשב פיקודי, חזרה ממיוחדת מעל 21 יום, ראיון מחופשת אבל, תב"ן שלילי(90+)- יש לבדוק קטלוג+עמידה בSILA+ דרגת מראיין+תוכן ענייני ומספק. עבור קשב פיקודי- לבדוק אזכור נוהל דלת פתוחה ביחידה, עבור תבין שלילי - יש לציין מסקנות להמשך טיפול (וה"ל,קיצור תב"ן או המשך בחינת התנהגות)',index:12},{quatsion:'חופשות- האם קיים תכנון שנתי ועדכון במידה ואין עמידה ביעדי הניצול, האם קיים מעקב אחר ניצול ברמת עבודה שוטפת יחידות משנה, יש לבדוק אקראית אסמכתאות חופשת מחלה לאנשי קבע. יש לבדוק ניצול יתר (מינוס) לחיילי חובה וחוסר ניצול לחיילי חובה יש לבדוק סגירת שנה דרך מערכת "אנשים".',index:13},{quatsion:"אירועי דגל ותחקירים- האם אותרו מקרים של פגיעה בזכויות הפרט (הזנת נפקדות לא תקינה, טיפול לא תקין בהליך קליטת חייל, חוסר בביקורים מאושפזכלואנפקד)? האם בוצע תחקיר ברמת מפקד היחידה האם נכתבה פעולה מתקנת נוסף על פעולת התיקון (פעולה מתקנת=נוהל למניעת הישנות התקלה, פעולת תיקון=פעולה לצמצום הנזק שנגרם במקרה ספציפי והשבה למצב תקין)",index:14}]},{name:"טיפוח ופיתוח אנשי קבע",list:[{quatsion:"העברת מידע - מה מנגנון הפצת מידע למיצוי זכויות ביחידה (קבוצת וואטסאפטלגרם, דפי מידע), האם טיפוח ופיתוח מבוצעות הסברות תקופתיות, מנגנון קליטה לאנשי קבע חדשים ביחידה. האם בוצעה הסברה בנושא שכר",index:15},{quatsion:'מיצוי זכויות אנ"ק - האם יש חוסרים בהזנות גני ילדים, מנגנון הזנת הוצאות נסיעה ברכב (טופס תקין ומלא, צירוף רשיון נהיגה, רישיון רכב וביטוח רכב- הזנת ת סיום לפי המצורפים), האם יש מאמץ לסייע ביציאה ללימודים של אנ"ק והאם יש אנ"ק ממתינים לגמר"ש',index:16}]},{name:"מוכוונות לפרט",list:[{quatsion:'כנס חיילים חדשים - האם קיים נוהל המגדיר פרקי זמן מינימליים לעריכת כנס בעלי התפקידים המציגים והאם התוכן מלא (אפסנאות-כביסהדוארציוד, מ מחנה-שקיים, מועדון, חדר -כושר, מסדרים ותורניות, מ.תש-זכויות תש, תשמיש, טופס 55 מ. חינוך-בגרויות, פסיכומטרי, הווי יחידה, ק משא- קורסים, חופשות, ימי ד, חו"ל, מוכוונות לפרט, דלת-פתוחה רופאחובש- נהלי מרפאה, קניו-נהלי טיפול ברה"ן)',index:17},{quatsion:'שנ"ב האם החייל קיבל את כלל ימי החפש"ש המגיעים לו טרם הכניסה לשנ"ב? לוחמים זכאים לניצול גם בסיום השנ"ב, האם מתבצעת בקרה (חפש"ש - רמ"פ א+ 14 ימים, רמ"ב אי 7 ימים)',index:18},{quatsion:"עובדי משמרות - בדיקה מול עובדי משמרות כי מבצעים משמרות סדורות- בדיקה מול דוח 1 ודיווחי חופשה במערכת בדיקה שעובדי המשמרות נמצאים בבסיס בכל תקופת המשמרת",index:19}]},{name:"שימור ידע",list:[{quatsion:"שמירת נתונים כיצד כל הידע והפיתוח כח שלך ישאר גם לבא אחריך? האם אתה שומר קבצים חשובים בתקייה מיועדת?",index:20},{quatsion:"חפיפה והכשרה- כיצד ענווה החפיפה ישלן לתפקידי איך התחברת לכל הנעשה והמשימות הפתוחות?",index:21}]},{name:"מסופחים",list:[{quatsion:'כליאה- האם בוצע ראיון חזרה מספק האם כולל ציון ימי תב"ן ועיכוב דרגה? תיעוד הביקורים ובדיקה אל מול החייל האם בוקר על ידי מפקדויו?',index:22},{quatsion:"מאושפזים- האם בוצע ראיון חזרה מספק! האם החייל בוקר על ידי מפקדיו: תיעוד ביקורים ושיחות טלפון ובדיקה אל מול החייל האם בוצעו ביקורים ושיחות טלפוניות בזמני התקן.",index:23}]},{name:"שחרורים",list:[{quatsion:'שחרורים- בדיקת הזנות חוו"ד 60 ימים לפני ת.שחרור. בדיקת חוגר גזור ומסירת שובר נסיעה/וט.ויתור, נוהל חפש"ש ביחידה',index:24}]},{name:"נפקדים ביחידה",list:[{quatsion:'נפקדים- בדיקת טפסי דיווח (אימות חתימת מפקד), ביקורי בית ומאמצי היחידה להשבת הנפקד (ביקור ראשון תוך 3 ימים מתחילת ההיעדרות או 48 שעות אם נושא נשק), טופס שיפוט תקין בדגש על התאמת תאריכים לדו"ח 1. במידה ולא אושרה עריקות מעל 10 ימים לאחר מועד הכרזת עריקות יש להציג התכתבויות עם מדור עריקים.',index:25}]},{name:'ממשקי עבודה מג"ד',list:[{quatsion:"פיתוח ובנין כח- האם יש תוכנית רב שנתית הכוללת פיתוח לוחמים וחיילים! האם קיים מעקב ניהול יחידתי שוטף ונוהל מוגדר ביחידה לגבי פיתוח פיקוד וקצונה",index:26},{quatsion:'חוסרי תקמ"צ (מעבר שיבוץ משרות, משרות לא מאוישות- דרישות תגבור ופיתוח הכשרה מקצועית)',index:27},{quatsion:'נתוני קצונה (האם קיים נוהל מסודר לאיתור פוטנציאל קצונה, איתור פוטנציאל ע"ב סקר תקופתי וסוציומטרי, איסוף חוו"ד מפקדים מסודר ותיעוד ראיונות איתור)',index:28},{quatsion:'הוצאה לקורס מ"כים, פיתוח מ"פ (האם קיים נוהל מסודר לאיתור פוטנציאל קצונה, איתור פוטנציאל ע"ב סקר תקופתי וסוציומטרי, איסוף חוו"ד מפקדים מסודר ותיעוד ראיונות איתור)',index:29},{quatsion:'יציאה למלט"ק יציאה לקמ"פ.',index:30},{quatsion:'שליטה בנתוני כ"א- פע על חוסרים כיצד נמצא בבקרה על נתוני דרישות כ"א.',index:31},{quatsion:"ועדות פרט – כל כמה זמן מתבצע (לפחות פעם בשלישון)? האם יש טבלת בקרה ומעקב: האם יש תאריכי סטאטוס לגמר ביצוע משימות! תוכן עשיר והצפת בעיות מהשטח! אימות נתונים וביצוע משימות- הצגת נתונים מרעדה קודמת בפתיחה",index:32},{quatsion:"פורום אנוש- כל כמה זמן מתבצע (לפחות פעם בשלישון? האם יש טבלת בקרה ומעקב? האם יש תאריכי סטאטוס לגמר ביצוע משימות! תוכן עשיר והצפת בעיות מהשטח! אימות נתונים וביצוע משימות- הצגת נתונים בפורום קודם בפתיחה",index:33},{quatsion:"תחקיר עריקים- סיכום מסקנות והמלצות מפקד יחידה ומעקב על ביצוע הוראות. האם קיימת למידה מתמדת ממקרי עריקות ביחידה",index:34}]},{name:"ממשקי עבודה מפקדי יחידות משנה",list:[{quatsion:'מעורבות מפקדים כללית- האם קיימות מעורבות בפקדים (מסירת ראיונות, ביקורי ביתשיחות טלפוניות, הזנות דו"ח 1)',index:35}]},{name:"ממשקי עבודה מפקדה ממנונה",list:[{quatsion:'מעורבות מפקדה ממונה- האם קיים שיח תקין עם המפקדה הממונה על תגבור כ"א, ופיתוח מקצועי ופיקודי האם קיימות חפיפה והדרכה מהמפקדה הממונה',index:36}]},{name:'ממשקי עבודה וחניכה של סגלי משא"ן תחתיו',list:[{quatsion:"חניכה מקצועית ואישית- האם בפתח את חייליו, מבצע בקרה וכך גם בווידא אותם ונותןלהם טיפיםי קיים פע עם החוליים אחת לשבוע?",index:37},{quatsion:"חניכת יעודי לקצונה- האם ישנו מעבר על פקלון יעודי לקצונה? האם החניך נכנס ללוזים בראשות מפקד היחידה? האם החניך מבצע תהליכים במערכת עם הדרכתך?",index:38},{quatsion:"פיתוח מקצועי. האם חייליך מודעים לעובדה שיכולים לצאת לקצונה? יש לך סטטוס על כל אחד?",index:39}]},{name:'משרד ת"ש',list:[{quatsion:'עוגנים מרכזיים בתחום בשנה האחרונה לרבות יוזמות, ממשקי עבודה מחלקת משא"ן, ממשקי עבודה דרג פיקוד וכו',index:40},{quatsion:'בקיאות מקצועית ואישית- האם מתקיים פע מקצועי בנושאי האחריות של המש"קיות? כל כמה זמן?',index:41}]},{name:"משרד חינוך",list:[{quatsion:'עוגנים מרכזיים בתחום בשנה האחרונה לרבות יוזמות, ממשקי עבודה מחלקת משא"ן, ממשקי עבודה דרג פיקוד וכו',index:42},{quatsion:'בקיאות מקצועית ואישית- האם מתקיים פע מקצועי בנושאי האחריות של המש"קיות? כל כמה זמן?',index:43}]},{name:"היערכות לחירום",list:[{quatsion:'פקודת חירום- האם קיימת פקודת חירום? האם קיימת בקיאות חיילי המשא"ן בפקודת חירום (יש לבדוק בשיחה עם חיילי המשא"ן), האם קיימים נאמני כ"א ביחידות המשנה?',index:44},{quatsion:'שבצ"ק- האם קיים וידוא סדיר (מסדר אמצעי זיהוי), האם קיים שבצ"ק מעודכן ונוהל לעדכונו ביחידה',index:45}]},{name:"משואה (ביבשה בלבד)",list:[{quatsion:"משואה- האם קיימת בקיאות במערכת משואה? אילו נתונים ק.משאיין אחראי להזנה",index:46}]},{name:"התרשמות חיילים",list:[{quatsion:"שיחות חיילים (אקראיותנושאיות)- האם שיח החיילים היה תואם את דברי הקצין? האם החיילים העלו נתונים מדאיגים?",index:47}]},{name:"יוזמות ביחידה",list:[{quatsion:'יוזמות מיוחדות- האם לקצין המשא"ן היו יוזמות בנושאים עליו אחראי? האם מפתח את העשייה?',index:48}]},{name:"תיקון ליקויים",list:[{quatsion:"תיקון ליקויים- האם נעשתה למידה מביקורות קודמות, האם היחידה הגישה תיקון ליקויים מביקורת קודמתי",index:49}]},{name:"חווד מפקד",list:[{quatsion:"התרשמות כללית מכל הביקורת גם מילולית",index:50}]}];export{tash,miloeim,sadir};let precentegMiloeim=[];// function helper() {
